import React, { useEffect, useState } from 'react';
import { Label, Select } from 'flowbite-react';
import axios from 'axios';

const AreaForm = () => {
    const [provinces, setProvince] = useState([]);
    const [provinceId, setProvinceId] = useState('');
    const [districts, setDistrict] = useState([]);
    const [districtId, setDistrictId] = useState('');
    const [wards, setWard] = useState([]);

    useEffect(() => {
        const getProvince = async () => {
            try {
                const res = await axios.get("https://provinces.open-api.vn/api/?depth=1");
                console.log(res.data);

                setProvince(res.data);
            } catch (ex) {
                console.error('Error fetching provinces: ', ex);
            }
        }
        getProvince();
    }, []);

    return (
        <div className="grid grid-cols-2 gap-5">
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="province" className="block font-bold mb-1">
                        Tỉnh/Thành phố
                    </Label>
                </div>
                <Select id="province" required >
                    <option selected value="">
                        Chọn tỉnh/thành phố
                    </option>
                    {provinces.map((resProvince, index) => (
                        <option key={index} value={resProvince.code}>
                            {resProvince.name}
                        </option>
                    ))}
                </Select>
            </div>
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="district" className="block font-bold mb-1">
                        Quận/Huyện
                    </Label>
                </div>
                <Select id="district" required onChange={handleDistrict} defaultValue="">
                    <option selected value="">
                        Chọn quận/huyện
                    </option>
                    {districts.map((resDistrict, index) => (
                        <option key={index} value={resDistrict.code}>
                            {resDistrict.name}
                        </option>
                    ))}
                </Select>
            </div>
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="ward" className="block font-bold mb-1">
                        Phường/Xã
                    </Label>
                </div>
                <Select id="ward" required >
                    <option selected value="">
                        Chọn phường/xã
                    </option>
                    {wards.map((resWard, index) => (
                        <option key={index} value={resWard.code}>
                            {resWard.name}
                        </option>
                    ))}
                </Select>
            </div>
        </div>
    );
};

export default AreaForm;
