import { Button, Card, Navbar } from 'flowbite-react';
import React, { useEffect, useState } from 'react';
import imgLogo from "../../resources/logoOupia.svg";
import { Link } from 'react-router-dom';
import { BsChevronLeft } from "react-icons/bs";

const SignUpFrom = () => {
    const [component, setComponent] = useState();
    const [step, setStep] = useState(0);

    const handleNextStep = () => {
        setStep(prev => prev + 1);
    }

    useEffect(() => {

    }, [step])

    return (
        <Card className="w-1/4 border-transparent pb-10 pt-7 shadow-lg">
            <Navbar.Brand className="mx-auto relative">
                <Button onClick={handleNextStep} className="text-blueTemplate absolute -left-28">
                    <BsChevronLeft size={20}/>
                </Button>
                <img
                    alt="Oupia Logo"
                    className="mr-3 h-7 sm:h-10"
                    src={imgLogo}
                />
                <p className="self-center whitespace-nowrap text-3xl text-blueTemplate font-bold dark:text-white">
                    Oupia
                </p>
            </Navbar.Brand>
            <form className="flex flex-col gap-4 mt-8">
                <Button onClick={handleNextStep} className="bg-blueTemplate">
                    <p className="font-bold text-base">Tiếp tục</p>
                </Button>
            </form>
            <hr class=" h-px my-2 bg-gray-200 border-0 dark:bg-gray-700" />
            <p className="font-thin text-sm mx-auto text-gray-900">Bạn có tài khoản? <Link to="/login" className="font-bold">Đăng nhập</Link></p>
        </Card>
    );
};

export default SignUpFrom;
