import React, { useEffect, useState } from 'react';
import { Label, Select } from 'flowbite-react';

const AreaForm = () => {
    const [provinces, setProvince] = useState([]);
    const [provinceId, setProvinceId] = useState('');
    const [districts, setDistrict] = useState([]);


    useEffect(() => {
        const getProvince = async () => {
            try {
                const res = await fetch("https://provinces.open-api.vn/api/?depth=1");
                const resProvince = await res.json();
                setProvince(resProvince);
            } catch (ex) {
                console.error('Error fetching provinces: ', ex);
            }
        }
        getProvince();
    }, []);

    const handleProvince = (evt) => {
        const getProvinceId = evt.target.value;
        setProvinceId(getProvinceId);
    }

    useEffect(() => {
        const getDistrict = async () => {
            try {
                const res = await fetch(`https://provinces.open-api.vn/api/d?province_code=${provinceId}`);
                const resDistrict = await res.json();
                setDistrict(resDistrict);
            } catch (ex) {
                console.error('Error fetching districts: ', ex);
            }
            getDistrict();
        }
    }, []);

    return (<>
        <div className="max-w-md" id="select">
            <div className="mb-2 block">
                <Label htmlFor="province" value="Tỉnh/Thành phố" onChange={(e) => handleProvince(e)} />
            </div>
            <Select id="province" required>
                {provinces.map((resProvince, index) => (
                    <option key={index} value={resProvince.code}>
                        {resProvince.name}
                    </option>
                ))}
            </Select>
        </div>
        <div className="max-w-md" id="select" >
            <div className="mb-2 block">
                <Label htmlFor="district" value="Quận/Huyện" />
            </div>
            <Select id="district" required>
                {districts.map((resDistrict, index) => (
                    <option key={index} value={resDistrict.code}>
                        {resDistrict.name}
                    </option>
                ))}
            </Select>
        </div >
    </>
    );
};

export default AreaForm;
