import { Breadcrumb, Button, Card, Checkbox, Label, TextInput } from 'flowbite-react';
import { HiHome } from 'react-icons/hi';
import React, { Fragment, useState } from 'react';
import { Link } from 'react-router-dom';
import PostForm from '../../components/PostForm/index';
import './style.scss'
import StepperButton from '../../components/StepperButton';
import { TiTick } from 'react-icons/ti';

const Upload = () => {
    const steps = ["Khu vực", "Thông tin mô tả", "Xác thực", "Hoàn thành"];
    const [currentStep, setCurrentStep] = useState(1);
    const [complete, setComplete] = useState(false);


    return (<Fragment>
        <Breadcrumb className="m-5 ">
            <Breadcrumb.Item icon={HiHome}>
                <Link to="/" className=" font-black dark:text-white">
                    Trang chủ
                </Link>
            </Breadcrumb.Item>
            <Breadcrumb.Item >
                <p className="text-700 text-darker font-black dark:text-white">
                    Đăng tin
                </p>
            </Breadcrumb.Item>
        </Breadcrumb>

        <div>
            <div className="flex justify-center">
                {steps?.map((step, i) => (
                    <div
                        key={i}
                        className={`step-item ${currentStep === i + 1 && "active"} ${(i + 1 < currentStep || complete) && "complete"
                            }`}
                    >
                        <div className="step">
                            {i + 1 < currentStep || complete ? <TiTick size={24} /> : i + 1}
                        </div>
                        <p className="text-gray-500">{step}</p>
                    </div>
                ))}
            </div>
        </div>
        <Card>
            <form className="flex flex-col gap-4">

                <PostForm currentStep={currentStep} stepsLength={steps.length}></PostForm>

                <StepperButton
                    currentStep={currentStep}
                    stepsLength={steps.length}
                    complete={complete}
                    setComplete={setComplete}
                    setCurrentStep={setCurrentStep}
                />

            </form>
        </Card>
    </Fragment>

    )
};

export default Upload;
