import { Box, Container, MenuItem, Typography, styled } from '@mui/material';
import logoImg from '../resources/logoOupia.svg';
import React from 'react';
import CustomButton from '../components/CustomButton';

const Header = () => {
    const NavLink = styled(Typography)(({ theme }) => ({
        fontSize: "14px",
        fontFamily: "Comfortaa",
        fontWeight: 700,
        cursor: "pointer",
        "&:hover": {
            color: "#FFF",
        },
    }));

    const NavBarLinksBox = styled(Box)(({ theme }) => ({
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        gap: theme.spacing(3),
        [theme.breakpoints.down("md")]: {
            display: "none",
        },
    }));

    const NavBarContainer = styled(Container)(({ theme }) => ({
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: theme.spacing(5),
        background: "#FF0AFB",
        [theme.breakpoints.down('md')]: {
            padding: theme.spacing(2),
        },
    }))

    const NavBarLogo = styled("img")(({ theme }) => ({
        width: "25px",
        cursor: 'pointer',
        [theme.breakpoints.down('md')]: {
            display: "none",
        }
    }))

    return <NavBarContainer>
        <Box sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "2.5rem"
        }}
        >
            <Box sx={{ display: "flex", alignItems: "center" }}>
                <NavBarLogo src={logoImg} alt="logo" />
            </Box>

            <NavBarLinksBox>
                <NavLink variant="body2">Trang chủ</NavLink>
                <NavLink variant="body2">Diễn đàn</NavLink>
                <NavLink variant="body2">Đăng bài</NavLink>
                <NavLink variant="body2">Feedback</NavLink>
                <NavLink variant="body2">Liên hệ</NavLink>
            </NavBarLinksBox>
        </Box>
        <Box sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
        }} >
            <NavLink variant='body2'>Đăng nhập</NavLink>
            <CustomButton
                backgroundColor="#0F1B4C"
                color="#FFF"
                buttonText= "Đăng ký"/>
        </Box>
    </NavBarContainer >


}


export default Header;
