import React, { useEffect, useState } from 'react';
import { Label, Select } from 'flowbite-react';
import axios from 'axios';
import "./style.scss";

const AreaForm = () => {
    const [provs, setProvs] = useState([]);
    const [provCode, setProvCode] = useState('');
    const [dists, setDists] = useState([]);

    const handleProvince = async (evt) => {
        const selectedProvCode = evt.target.value;
        setProvCode(selectedProvCode);
    }

    useEffect(() => {
        const getProvince = async () => {
            try {
                const res = await axios.get("https://provinces.open-api.vn/api/?depth=1");
                const resProv = await res.data;
                setProvs(resProv);
            } catch (ex) {
                console.error('Error fetching provinces: ', ex);
            }
        }
        getProvince();
    }, []);

    useEffect(() => {
        const getDistrict = async () => {
            try {
                const res = await axios.get(`https://provinces.open-api.vn/api/p/${provs.code}?depth=2`);
                const resDist = await res.data.districts
                setDists(resDist);
            } catch (ex) {
                console.error('Error fetching provinces: ', ex);
            }
        }
        getDistrict()
    }, [provs]);

    return (
        <>
            <h2 className="text-2xl"> Thông tin khu vực </h2>
            <div className="grid grid-cols-2 gap-5">
                <div className="max-w-md" id="select">
                    <div className="mb-2 block">
                        <Label htmlFor="province" className="block font-bold mb-1">
                            Tỉnh/Thành phố
                        </Label>
                    </div>
                    <Select id="province" required onChange={handleProvince} >
                        <option selected value="">
                            --Chọn tỉnh/thành phố--
                        </option>
                        {provs.map((resProv, index) => (
                            <option key={index} value={resProv.code}>
                                {resProv.name}
                            </option>
                        ))}
                    </Select>
                </div>
                <div className="max-w-md" id="select">
                    <div className="mb-2 block">
                        <Label htmlFor="province" className="block font-bold mb-1">
                            Quận/Huyện
                        </Label>
                    </div>
                    <Select id="province" required >
                        <option selected value="">
                            --Chọn quận/huyện -- 
                        </option>
                        {dists.map((resDist, index) => (
                            <option key={index} value={resDist.code}>
                                {resDist.name}
                            </option>
                        ))}
                    </Select>
                </div>
            </div>
        </>
    );
};

export default AreaForm;
