import React from 'react';
import "./style.scss";
import { Avatar } from 'flowbite-react';

const UserProfile = () => {
    const user = {
        fullName : "Nguyễn Kim Bảo Ngân",
        follows : "125",
        posts : "46",
        avatar: "https://scontent.fsgn3-1.fna.fbcdn.net/v/t39.30808-1/350118519_479477941022130_6544855667418265188_n.jpg?stp=dst-jpg_s320x320&_nc_cat=111&ccb=1-7&_nc_sid=2b6aad&_nc_ohc=i3JyyOmw0B0AX9xslvR&_nc_ht=scontent.fsgn3-1.fna&oh=00_AfBYMGvrA3cGTvfGGX_oUXHZRnPQS2sQipFFKhar9TLx9w&oe=64E0EB9D",
    }

    return (
        <div>
            <div className="relative bg-wallpaper">
                <div className="absolute img-translate-y top-full z-999 left-20 w-56 h-56 ring-[5px] ring-white rounded-full shadow-xl">
                    <img
                        src={user.avatar}
                        alt="Avatar"
                        className="w-full h-full rounded-full"
                    />
                </div>
            </div>
            <div className="flex flex-col px-96 mt-5 gap-2">
                <h1 className="text-3xl font-bold text-left">{user.fullName}</h1>
                <div className="flex">
                    <h3 className="text-gray-700 font-bold">{user.follows} người theo dõi</h3>
                    </div>
            </div>
            <div className="h-screen"></div>
        </div>
    );
};

export default UserProfile;
