import { Box, Container, MenuItem, Typography, styled } from '@mui/material';
import logoImg from '../resources/logoOupia.svg';
import React from 'react';
import CustomButton from '../components/CustomButton';
import { Link } from 'react-router-dom';

const Header = () => {
    const NavLink = styled(Typography)(({ theme }) => ({
        fontSize: "16px",
        fontFamily: "Comfortaa",
        fontWeight: 700,
        cursor: "pointer",
        color: "#FFF",
        "&:hover": {
            color: "#00B7FF",
        },
    }));

    const NavBarLinksBox = styled(Box)(({ theme }) => ({
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        gap: theme.spacing(3),
        [theme.breakpoints.down("md")]: {
            display: "none",
        },
    }));

    const NavBarContainer = styled(Container)(({ theme }) => ({
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        width: "100%",
        maxWidth: " 99999px !important",
        margin: 0,
        padding: theme.spacing(5),
        [theme.breakpoints.down('md')]: {
            padding: theme.spacing(2),
        },
        background: "#000"
    }))

    const NavBarLogo = styled("img")(({ theme }) => ({
        width: "35px",
        cursor: 'pointer',
        [theme.breakpoints.down('md')]: {
            display: "none",
        }
    }))

    return <NavBarContainer>
        <Box sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "2.5rem",
        }}
        >
            <Box sx={{ display: "flex", alignItems: "center" }}>
                <NavBarLogo src={logoImg} alt="logo" />
            </Box>

            <NavBarLinksBox>
                <Link to="/" style={{textDecoration: "none"}}>
                    <NavLink variant="h2">Trang chủ</NavLink>
                </Link>
                <NavLink variant="body1">Diễn đàn</NavLink>
                <NavLink variant="body1">Đăng bài</NavLink>
                <NavLink variant="body1">Feedback</NavLink>
                <NavLink variant="body1">Liên hệ</NavLink>
            </NavBarLinksBox>
        </Box>
        <Box sx={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
        }} >
            <Link to="/login" style={{textDecoration: "none"}}>
                <CustomButton
                    backgroundColor="#00B7FF"
                    color="#FFF"
                    buttonText="Đăng Nhập"
                />
            </Link>
        </Box>
    </NavBarContainer >


}


export default Header;
