import { Tabs } from 'flowbite-react';
import React from 'react';
import { LuEdit, LuHeart } from "react-icons/lu";
import { IoImageOutline } from "react-icons/io5";
import "./style.scss";
import { unstable_HistoryRouter , useLocation } from 'react-router-dom';

const TabProfile = () => {
    const history = unstable_HistoryRouter();
    const location = useLocation();

    const handleTabClick = (to) => {
        history.push(to);
    };

    return (
        <Tabs.Group style='underline'>
            <Tabs.Item
                icon={LuEdit}
                title="Bài viết"
                active={location.pathname === '/posts'}
                onClick={() => handleTabClick('/posts')}
            />
            <Tabs.Item
                icon={LuHeart}
                title="Yêu thích"
                active={location.pathname === '/favourites'}
                onClick={() => handleTabClick('/favourites')}
            />
            <Tabs.Item
                icon={IoImageOutline}
                title="Hình ảnh"
                active={location.pathname === '/photos'}
                onClick={() => handleTabClick('/photos')} />
        </Tabs.Group >
    );
};

export default TabProfile;
