import { Avatar, FileInput, Label, TextInput } from 'flowbite-react';
import React, { useState } from 'react';

const StepThree = () => {
    const [avatarImg, setAvatarImg] = useState('https://pixlok.com/wp-content/uploads/2021/03/default-user-profile-picture.jpg');

    const handleFileChange = (event) => {
        if (event.target.files && event.target.files[0]) {
            setAvatarImg(URL.createObjectURL(event.target.files[0]));
        }
    };


    return (
        <>
            <div className="w-40 h-40 ring-[5px] ring-white rounded-full shadow-xl mx-auto">
                <img
                    src={avatarImg}
                    alt="Avatar"
                    className="w-full h-full rounded-full"
                />
            </div>
            <div className="mt-10">
                <div className="mb-2 block">
                    <Label className="text-lg mt-2" htmlFor="username" value="Tên người dùng" />
                </div>
                <TextInput id="username" required shadow type="text" />
            </div>
            <div className="grid grid-cols-2 gap-5 mt-3">
                <div>
                    <div className="mb-2 block">
                        <Label className="text-lg mt-2" htmlFor="password" value="Mật khẩu" />
                    </div>
                    <TextInput id="password" required shadow type="password" />
                </div>
                <div>
                    <div className="mb-2 block">
                        <Label className="text-lg mt-2" htmlFor="confirm" value="Xác nhận mật khẩu" />
                    </div>
                    <TextInput id="confirm" required shadow type="password" />
                </div>
            </div>
            <div className="mt-3">
                <div id="avatar">
                    <div className="mb-2 block">
                        <Label className="text-lg mt-2" htmlFor="avatar" value="Ảnh đại diện" />
                    </div>
                    <FileInput id="avatar" onChange={handleFileChange} />
                </div>
            </div>
        </>
    );
};

export default StepThree;
