import React, { useEffect, useState } from 'react';
import { Label, Select } from 'flowbite-react';

const AreaForm = () => {
    const [provinces, setProvince] = useState([]);
    const [provinceId, setProvinceId] = useState('');
    const [districts, setDistrict] = useState([]);
    const [districtId, setDistrictId] = useState('');
    const [wards, setWard] = useState([]);


    useEffect(() => {
        const getProvince = async () => {
            try {
                const res = await fetch("https://provinces.open-api.vn/api/?depth=1");
                const resProvince = await res.json();
                setProvince(resProvince);
            } catch (ex) {
                console.error('Error fetching provinces: ', ex);
            }
        }
        getProvince();
    }, []);

    const handleProvince = async (evt) => {
        const getProvinceId = evt.target.value;
        setProvinceId(getProvinceId);
    }


    useEffect(() => {
        const getDistrict = async () => {
            try {
                const res = await fetch(`https://provinces.open-api.vn/api/p/${provinceId}/depth=2`);
                const resDistrict = await res.json();
                setDistrict(resDistrict);
            } catch (ex) {
                console.error('Error fetching districts: ', ex);
            }
        }
        if (provinceId !== '') {
            getDistrict();
        }
    }, [provinceId])


    const handleDistrict = async (evt) => {
        const getDistrictId = evt.target.value;
        setDistrictId(getDistrictId);
    }

    useEffect(() => {
        const getWard = async () => {
            try {
                const res = await fetch(`https://provinces.open-api.vn/api/d/${districtId}?depth=2`);
                const resProvince = await res.json();
                setWard(resProvince);
            } catch (ex) {
                console.error('Error fetching wards: ', ex);
            }
        }
        if(districtId != null){
            getWard();
        }
    }, [districtId]);

    return (
        <>
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="province" className="block font-bold mb-1">
                        Tỉnh/Thành phố
                    </Label>
                </div>
                <Select id="province" required onChange={handleProvince}>
                    <option selected value="">
                        Chọn tỉnh/thành phố
                    </option>
                    {provinces.map((resProvince, index) => (
                        <option key={index} value={resProvince.code}>
                            {resProvince.name}
                        </option>
                    ))}
                </Select>
            </div>
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="district" className="block font-bold mb-1">
                        Quận/Huyện
                    </Label>
                </div>
                <Select id="district" required onChange={handleDistrict}>
                    <option selected value="">
                        Chọn quận/huyện
                    </option>
                    {districts.map((resDistrict, index) => (
                        <option key={index} value={resDistrict.code}>
                            {resDistrict.name}
                        </option>
                    ))}
                </Select>
            </div>
            <div className="max-w-md" id="select">
                <div className="mb-2 block">
                    <Label htmlFor="ward" className="block font-bold mb-1">
                        Phường/Xã
                    </Label>
                </div>
                <Select id="ward" required >
                    <option selected value="">
                        Chọn phường/xã
                    </option>
                    {wards.map((resWard, index) => (
                        <option key={index} value={resWard.code}>
                            {resWard.name}
                        </option>
                    ))}
                </Select>
            </div>
        </>
    );
};

export default AreaForm;
