import { Button, Navbar } from 'flowbite-react';
import imgLogo from "../../resources/logoOupia.svg";
import { Link, NavLink, useLocation } from 'react-router-dom';
import "./style.scss";
import { useEffect, useState } from 'react';

const Header = () => {
    const [bgColor, setBgColor] = useState(false);

    const location = useLocation();
    const isHome = location.pathname === '/';

    useEffect(() => {
        const handleScroll = () => {
            if (window.scrollY > 0) {
                setBgColor(true);
            } else {
                setBgColor(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <>
            <div className={bgColor ? "header header-bg" : "header"}>
                <Navbar
                    fluid
                    rounded className="py-5 bg-transparent">
                    <Navbar.Brand className="mr-5">
                        <Link to="/">
                            <img
                                alt="Flowbite React Logo"
                                className="mr-3 h-6 sm:h-9"
                                src={imgLogo}
                            />
                        </Link>
                        <Link to="/">
                            <p className="self-center whitespace-nowrap text-2xl font-black font-bold dark:text-white">
                                Oupia
                            </p>
                        </Link>
                    </Navbar.Brand>

                    <div className="flex md:order-2 items-center">
                        <Link to="/register" className="mr-5">
                            Đăng ký
                        </Link>
                        <Link to="/login">
                            <Button color="dark">Đăng nhập</Button>
                        </Link>
                    </div>

                    <Navbar.Collapse >
                        <NavLink to="/" className="text-lg">
                            Trang chủ
                        </NavLink>
                        <NavLink to="/find" className="text-lg">
                            Tìm phòng
                        </NavLink>


                        <NavLink to="/upload" className="text-lg">
                            Đăng tin
                        </NavLink>

                        <NavLink to="/upload" className="text-lg">
                            Pricing
                        </NavLink>
                        <NavLink to="/upload" className="text-lg">
                            Contact
                        </NavLink>
                    </Navbar.Collapse>
                </Navbar>
            </div>
            <Navbar
                fluid
                rounded className="py-5 ">
                <Navbar.Brand className="mr-5">
                    <Link to="/">
                        <img
                            alt="Flowbite React Logo"
                            className="mr-3 h-6 sm:h-9"
                            src={imgLogo}
                        />
                    </Link>
                    <Link to="/">
                        <p className="self-center whitespace-nowrap text-2xl font-black font-bold dark:text-white">
                            Oupia
                        </p>
                    </Link>
                </Navbar.Brand>
            </Navbar>
        </>

    );
};

export default Header;
